SET SQL_MODE="NO_AUTO_VALUE_ON_ZERO";
CREATE TABLE IF NOT EXISTS `admin_panel` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` varchar(31) NOT NULL,  `url` varchar(31) NOT NULL,  `icon_url` varchar(63) NOT NULL,  `place` tinyint(4) NOT NULL DEFAULT '0',  PRIMARY KEY (`id`),  UNIQUE KEY `name` (`name`,`url`),  KEY `place` (`place`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=12 ;
CREATE TABLE IF NOT EXISTS `article` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` varchar(127) NOT NULL,  `url` varchar(127) NOT NULL,  `content` text NOT NULL,  `created` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,  PRIMARY KEY (`id`),  UNIQUE KEY `url` (`url`),  KEY `created` (`created`)) ENGINE=MyISAM DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `blog` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` varchar(127) NOT NULL,  `created` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,  `summary` text NOT NULL,  `content` text NOT NULL,  PRIMARY KEY (`id`),  KEY `created` (`created`)) ENGINE=MyISAM DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `docs` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `filename` varchar(63) COLLATE latin1_general_ci NOT NULL DEFAULT '',  `added` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,  PRIMARY KEY (`id`),  UNIQUE KEY `filename` (`filename`),  KEY `added` (`added`)) ENGINE=MyISAM DEFAULT CHARSET=latin1 COLLATE=latin1_general_ci AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `gallery` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` varchar(127) COLLATE latin1_general_ci NOT NULL DEFAULT '',  `description` text COLLATE latin1_general_ci NOT NULL,  `category` int(11) NOT NULL DEFAULT '0',  PRIMARY KEY (`id`),  KEY `author` (`category`),  KEY `name` (`name`),  FULLTEXT KEY `description` (`description`)) ENGINE=MyISAM DEFAULT CHARSET=latin1 COLLATE=latin1_general_ci AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `gallery_cat` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` varchar(127) COLLATE latin1_general_ci NOT NULL DEFAULT '',  `thumb` int(11) NOT NULL DEFAULT '0',  PRIMARY KEY (`id`),  UNIQUE KEY `name` (`name`),  KEY `thumb` (`thumb`)) ENGINE=MyISAM DEFAULT CHARSET=latin1 COLLATE=latin1_general_ci AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `hours` (  `id` int(11) NOT NULL,  `name` varchar(12) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `months` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` varchar(12) NOT NULL,  `short` varchar(3) NOT NULL,  PRIMARY KEY (`id`),  UNIQUE KEY `name` (`name`),  UNIQUE KEY `short` (`short`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=13 ;
CREATE TABLE IF NOT EXISTS `site_bool` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `key` varchar(63) NOT NULL,  `value` tinyint(4) NOT NULL,  PRIMARY KEY (`id`), UNIQUE KEY `key` (`key`),  KEY `id` (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=3 ;
CREATE TABLE IF NOT EXISTS `site_integer` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `key` varchar(63) COLLATE latin1_general_ci NOT NULL,  `value` int(11) NOT NULL,  PRIMARY KEY (`id`),  UNIQUE KEY `key` (`key`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 COLLATE=latin1_general_ci AUTO_INCREMENT=9 ;
CREATE TABLE IF NOT EXISTS `site_text` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `key` varchar(63) COLLATE latin1_general_ci NOT NULL,  `value` text COLLATE latin1_general_ci NOT NULL,  PRIMARY KEY (`id`),  UNIQUE KEY `key` (`key`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 COLLATE=latin1_general_ci AUTO_INCREMENT=5 ;
CREATE TABLE IF NOT EXISTS `site_varchar` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `key` varchar(63) COLLATE latin1_general_ci NOT NULL,  `value` varchar(255) COLLATE latin1_general_ci NOT NULL,  PRIMARY KEY (`id`),  UNIQUE KEY `key` (`key`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 COLLATE=latin1_general_ci AUTO_INCREMENT=4 ;
CREATE TABLE IF NOT EXISTS `textual` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `nav_order` tinyint(4) NOT NULL DEFAULT '100',  `locked` tinyint(4) NOT NULL DEFAULT '0',  `hidden` tinyint(4) NOT NULL DEFAULT '0',  `section` varchar(40) COLLATE latin1_general_ci NOT NULL DEFAULT '',  `title` varchar(100) COLLATE latin1_general_ci NOT NULL DEFAULT '', `text` text COLLATE latin1_general_ci NOT NULL,  `meta_desc` text COLLATE latin1_general_ci NOT NULL,  `date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  PRIMARY KEY (`id`),  UNIQUE KEY `section` (`section`),  KEY `hidden` (`hidden`),  KEY `nav_order` (`nav_order`),  FULLTEXT KEY `text` (`text`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 COLLATE=latin1_general_ci AUTO_INCREMENT=11 ;
